import React, { Component } from 'react'

class SignIn extends Component {
  state = {
    email: '',
    password: ''
  }
  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
  }
  render() {
    return (
      <div className="container" Style="width:50%">
        <div className="card large blue-grey">
            <div className="card-content white-text" >
                <span className="card-title center"><strong>Sign in</strong></span>
                <form className="blue-gray" Style="padding:3% 10%" onSubmit={this.handleSubmit}>
                    <div className="input-field">
                        <label htmlFor="email">Email</label>
                        <input type="email" id='email'  ClassName="blue-gray" Style="padding:0 5px" onChange={this.handleChange} />
                    </div>
                    <div className="input-field blue-gray darken-1">
                        <label htmlFor="password">Password</label>
                        <input type="password" id='password'  Style="padding:0 5px" onChange={this.handleChange} />
                    </div>

                    <div className="input-field blue-gray">
                        <button className="btn pink lighten-1 z-depth-0">Login</button>
                    </div>
                </form>
            </div>
            <div className="card-action">
                <a href="#">This is a link</a>
                <a href="#">This is a link</a>
            </div>
        </div>
      </div>
    )
  }
}

export default SignIn
